mapper.select:
SELECT num, subject, sday, eday, stime, etime,
            color, repeat, repeat_cycle 
FROM schedule
WHERE userId = #{userId} AND 
   ( 
      ( 
         TO_DATE(sday, 'YYYYMMDD') >= TO_DATE(#{startDay}, 'YYYYMMDD')
             AND TO_DATE(sday, 'YYYYMMDD') <= TO_DATE(#{endDay}, 'YYYYMMDD')  
      ) OR (
         repeat=1 AND repeat_cycle!=0 AND 
	         TO_CHAR(ADD_MONTHS(sday, 12 * repeat_cycle*TRUNC(((SUBSTR(#{startDay},1,4)-SUBSTR(sday,1,4))/repeat_cycle))), 'YYYYMMDD') >= #{startDay} 
	         AND TO_CHAR(ADD_MONTHS(sday, 12 * repeat_cycle*TRUNC(((SUBSTR(#{startDay},1,4)-SUBSTR(sday,1,4))/repeat_cycle))), 'YYYYMMDD') <= #{endDay}
	  )
  )
  ORDER BY sday ASC, num DESC
